import React, { useState, useEffect } from 'react';
import Header from './components/Header.jsx';
import Hero from './components/Hero.jsx';
import Footer from './components/Footer.jsx';
import Modal from './components/Modal.jsx';

function App() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState({ title: '', body: '' });

  const handleNavClick = (section) => {
    const content = getModalContent(section);
    setModalContent(content);
    setIsModalOpen(true);
  };

  const handleWorkClick = () => {
    const content = getModalContent('portfolio');
    setModalContent(content);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const getModalContent = (section) => {
    const content = {
      resume: {
        title: 'Resume',
        body: `
          <div class="space-y-6">
            <div>
              <h4 class="text-xl font-semibold text-teal-500 mb-4">Professional Experience</h4>
              <p class="mb-3"><strong>Full Stack Developer</strong> - Tech Company (2022-Present)</p>
              <p class="text-portfolio-text-secondary mb-6">Developed and maintained web applications using modern technologies including React.js, Node.js, and various databases.</p>
            </div>
            
            <div>
              <h4 class="text-xl font-semibold text-teal-500 mb-4">Skills</h4>
              <p class="mb-3">• Frontend: React.js, JavaScript, HTML5, CSS3, Tailwind CSS</p>
              <p class="mb-3">• Backend: Node.js, Python, Express.js</p>
              <p class="mb-6">• Database: MongoDB, PostgreSQL, MySQL</p>
            </div>
            
            <div>
              <h4 class="text-xl font-semibold text-teal-500 mb-4">Education</h4>
              <p><strong>Computer Science Degree</strong> - University Name (2018-2022)</p>
            </div>
          </div>
        `
      },
      contact: {
        title: 'Contact Me',
        body: `
          <div class="space-y-6">
            <p class="mb-6">I'd love to hear from you! Let's connect and discuss opportunities.</p>
            
            <div class="mb-4">
              <strong class="text-teal-500">Email:</strong><br>
              <a href="mailto:saqlain@example.com" class="text-teal-500 hover:text-teal-400">saqlain@example.com</a>
            </div>
            
            <div class="mb-4">
              <strong class="text-teal-500">Phone:</strong><br>
              <span>+1 (555) 123-4567</span>
            </div>
            
            <div class="mb-6">
              <strong class="text-teal-500">Location:</strong><br>
              <span>City, State, Country</span>
            </div>
            
            <p class="text-portfolio-text-secondary">
              Feel free to reach out through any of the social media platforms linked in the footer as well!
            </p>
          </div>
        `
      },
      about: {
        title: 'About Me',
        body: `
          <div class="space-y-6">
            <p class="mb-4">
              Hello! I'm Saqlain Sheikh, a passionate full-stack developer with a love for creating 
              beautiful, functional, and user-friendly applications.
            </p>
            
            <p class="mb-4">
              I specialize in modern web technologies and enjoy working on challenging projects 
              that push the boundaries of what's possible on the web. My experience spans across 
              frontend and backend development, with a focus on React.js ecosystem.
            </p>
            
            <p class="mb-6">
              When I'm not coding, you can find me exploring new technologies, contributing to 
              open-source projects, or enjoying outdoor activities.
            </p>
            
            <div class="mb-4">
              <strong class="text-teal-500">What I Do:</strong>
            </div>
            <ul class="list-disc list-inside space-y-2">
              <li>Web Application Development</li>
              <li>UI/UX Design Implementation</li>
              <li>API Development & Integration</li>
              <li>Performance Optimization</li>
            </ul>
          </div>
        `
      },
      portfolio: {
        title: 'My Work',
        body: `
          <div class="space-y-6">
            <p class="mb-6">
              Here are some of my recent projects and accomplishments:
            </p>
            
            <div class="p-4 bg-blue-500/10 rounded-lg border border-blue-500/20 mb-5">
              <h5 class="text-lg font-semibold text-teal-500 mb-2">E-Commerce Platform</h5>
              <p class="text-portfolio-text-secondary mb-2">
                Full-stack e-commerce solution built with React.js and Node.js
              </p>
              <p class="text-xs text-portfolio-text-secondary">
                Technologies: React, Node.js, MongoDB, Stripe API
              </p>
            </div>
            
            <div class="p-4 bg-yellow-500/10 rounded-lg border border-yellow-500/20 mb-5">
              <h5 class="text-lg font-semibold text-teal-500 mb-2">Task Management App</h5>
              <p class="text-portfolio-text-secondary mb-2">
                Collaborative project management tool with real-time updates
              </p>
              <p class="text-xs text-portfolio-text-secondary">
                Technologies: React, Socket.io, Express.js, PostgreSQL
              </p>
            </div>
            
            <div class="p-4 bg-green-500/10 rounded-lg border border-green-500/20 mb-5">
              <h5 class="text-lg font-semibold text-teal-500 mb-2">Portfolio Website</h5>
              <p class="text-portfolio-text-secondary mb-2">
                This very website you're looking at right now!
              </p>
              <p class="text-xs text-portfolio-text-secondary">
                Technologies: React, Tailwind CSS, Design System
              </p>
            </div>
            
            <p class="text-portfolio-text-secondary text-center mt-6">
              More projects coming soon...
            </p>
          </div>
        `
      }
    };

    return content[section] || {
      title: 'Page Not Found',
      body: '<p>This section is coming soon!</p>'
    };
  };

  // Handle escape key to close modal
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && isModalOpen) {
        closeModal();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isModalOpen]);

  // Handle body scroll when modal is open
  useEffect(() => {
    if (isModalOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [isModalOpen]);

  return (
    <div className="min-h-screen flex flex-col">
      <Header onNavClick={handleNavClick} />
      <Hero onWorkClick={handleWorkClick} />
      <Footer />
      <Modal 
        isOpen={isModalOpen}
        onClose={closeModal}
        title={modalContent.title}
        body={modalContent.body}
      />
    </div>
  );
}

export default App;
