import React from 'react';

const Header = ({ onNavClick, activeSection }) => {
  const handleNavLinkClick = (e, section) => {
    e.preventDefault();
    onNavClick(section);
  };

  const isActive = (section) => activeSection === section;

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <h1 className="logo-text">Saqlain Sheikh</h1>
          </div>
          <nav className="nav">
            <ul className="nav-list">
              <li className="nav-item">
                <a 
                  href="#resume" 
                  className={`nav-link ${isActive('resume') ? 'text-portfolio-text-primary' : ''}`}
                  onClick={(e) => handleNavLinkClick(e, 'resume')}
                >
                  resume
                </a>
              </li>
              <li className="nav-item">
                <a 
                  href="#contact" 
                  className={`nav-link ${isActive('contact') ? 'text-portfolio-text-primary' : ''}`}
                  onClick={(e) => handleNavLinkClick(e, 'contact')}
                >
                  contact
                </a>
              </li>
              <li className="nav-item">
                <a 
                  href="#about" 
                  className={`nav-link ${isActive('about') ? 'text-portfolio-text-primary' : ''}`}
                  onClick={(e) => handleNavLinkClick(e, 'about')}
                >
                  about
                </a>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
