import React from 'react';

const Hero = ({ onWorkClick }) => {
  return (
    <main className="hero">
      <div className="container">
        <div className="hero-content">
          <p className="greeting-text">Hello, my name is</p>
          <h2 className="main-name">Saqlain</h2>
          <div className="welcome-section">
            <p className="welcome-text">I welcome you to see</p>
            <p 
              className="work-text"
              onClick={onWorkClick}
              role="button"
              tabIndex={0}
              onKeyDown={(e) => {
                if (e.key === 'Enter' || e.key === ' ') {
                  e.preventDefault();
                  onWorkClick();
                }
              }}
            >
              my work
            </p>
          </div>
        </div>
      </div>
    </main>
  );
};

export default Hero;
